import React, { Component } from 'react';
import './directory.styles.css';
import MenuItem from '../../components/menu-item/menu-item.component';
import { connect } from 'react-redux';

class Directory extends Component {

  componentDidMount(){
    this.props.getSections();
  }

    render() {
        return (
            <div className="directory-menu">
                {/* {this.state.sections.map(section => 
                    <MenuItem key={section.id} title={section.title} url={section.imageUrl} size={section.size}/>
                )} */}
                {this.props.sections.map(({id , ...otherSectionprops}) => 
                    <MenuItem key={id} {...otherSectionprops}/>
                )}
            </div>
        );
    }
}

const mapStateToProps = ({ sections : { directoryReducer }}) => ({
  sections : directoryReducer.sections
})


export default connect(mapStateToProps)(Directory);
