import { TOGGLE_CART_DROPDOWN, ADD_ITEM, REMOVE_ITEM, CLEAR_ITEM_FROM_CART, CLEAR_CART_SUCCESS, CLEAR_CART_FAILED } from '../../types/types';
import { addItemsToCart, removeItemFromCart } from './cart.utils';

const INITIAL_STATE = {
    hidden: 'true',
    cartItems: [],
    errorMessage: null
}

const CartReducer = (state = INITIAL_STATE, action) => {

    switch (action.type) {
        case TOGGLE_CART_DROPDOWN:
            return {
                ...state,
                hidden: !state.hidden
            }

        case ADD_ITEM:
            return {
                ...state,
                cartItems: addItemsToCart(state.cartItems, action.data)
            }

        case CLEAR_ITEM_FROM_CART:
            return {
                ...state,
                cartItems: state.cartItems.filter(CartItem => CartItem.id !== action.data.id)
            }

        case REMOVE_ITEM:
            return {
                ...state,
                cartItems: removeItemFromCart(state.cartItems, action.data)
            }

        case CLEAR_CART_SUCCESS:
            return {
                ...state,
                cartItems: [],
                errorMessage: null
            }

        case CLEAR_CART_FAILED:
            return {
                ...state,
                errorMessage: action.data
            }
        default:
            return state
    }

}

export default CartReducer;
