import { UPDATE_COLLECTION , FETCH_COLLECTIONS_FAILED ,FETCH_COLLECTIONS_START , FETCH_COLLECTIONS_SUCCESS } from '../../types/types';
import { firestore,ConvertCollectionsToMap } from '../../firebase/firebase.utils';


export const FetchCollectionsStart = () => ({
    type : FETCH_COLLECTIONS_START
})

export const FetchCollectionsStartAsync = () => {
    return dispatch => {
        const collectionRef = firestore.collection('collections')

    collectionRef.onSnapshot( async snapShot => {

      const collectionMap = ConvertCollectionsToMap(snapShot)
      UpdateCollection(collectionMap)
      this.setState({ loading : false})
    })
    }
}
