import {SET_CURRENT_USER} from "../../types/types";

const INITIAL_STATE = {
    currentUser : null
}

const userReducer = (state = INITIAL_STATE , action) => {
    switch(action.type)
    {
        case  SET_CURRENT_USER :
        return {
            ...state,
            currentUser : action.data
        }
        default :
        return state
    }
}

export default userReducer;
