import { TOGGLE_CART_DROPDOWN, ADD_ITEM, REMOVE_ITEM, CLEAR_ITEM_FROM_CART, CLEAR_CART_START, CLEAR_CART_SUCCESS, CLEAR_CART_FAILED } from '../../types/types';

export const toggleCartDropdown = () => ({
    type: TOGGLE_CART_DROPDOWN
})

export const addItem = item => ({
    type: ADD_ITEM,
    data: item
})

export const clearItem = item => ({
    type: CLEAR_ITEM_FROM_CART,
    data: item
})

export const removeItem = item => ({
    type: REMOVE_ITEM,
    data: item
})

export const clearCartStart = () => ({
    type: CLEAR_CART_START
})

export const clearCartSuccess = () => ({
    type: CLEAR_CART_SUCCESS
})

export const clearCartFailed = () => ({
    type: CLEAR_CART_FAILED
})
