import {SET_CURRENT_USER,GOOGLE_SIGNIN_START,SIGNIN_SUCCESS,SIGNIN_FAILED ,EMAIL_SIGNIN_START} from '../../types/types'

export const setCurrentUser = user => ({
    type : SET_CURRENT_USER,
    data : user
})

export const googleSigninStart = () => ({
    type : GOOGLE_SIGNIN_START
})

export const emailSigninStart = (emailAndPassword) => ({
    type : EMAIL_SIGNIN_START,
    data : emailAndPassword
})

export const signinSuccess = user => ({
    type : SIGNIN_SUCCESS,
    data : user
})

export const signinFailed = errorMessage => ({
    type : SIGNIN_FAILED,
    data : errorMessage
})
