import {SET_CURRENT_USER,GOOGLE_SIGNIN_START,GOOGLE_SIGNIN_SUCCESS,GOOGLE_SIGNIN_FAILED ,EMAIL_SIGNIN_START,EMAIL_SIGNIN_SUCCESS,EMAIL_SIGNIN_FAILED} from "../../types/types";

const INITIAL_STATE = {
    currentUser : null
}

const userReducer = (state = INITIAL_STATE , action) => {
    switch(action.type)
    {
        case GOOGLE_SIGNIN_SUCCESS :
        case EMAIL_SIGNIN_SUCCESS :   
        return {
            ...state,
            currentUser : action.data
        }

        case GOOGLE_SIGNIN_FAILED :
        case EMAIL_SIGNIN_FAILED :   
        return {
            ...state,
            currentUser : action.data
        }
        default :
        return state
    }
}

export default userReducer;
