import {SIGNIN_SUCCESS,SIGNIN_FAILED} from "../../types/types";

const INITIAL_STATE = {
    currentUser : null,
    errorMessage : null
}

const userReducer = (state = INITIAL_STATE , action) => {
    switch(action.type)
    {
        case SIGNIN_SUCCESS : 
        return {
            ...state,
            currentUser : action.data,
            errorMessage : null
        }

        case SIGNIN_FAILED : 
        return {
            ...state,
            errorMessage : action.data
        }
        default :
        return state
    }
}

export default userReducer;
