import { takeLatest, all, call, put } from 'redux-saga/effects';

import { googleSigninSuccess, googleSigninFailed , emailSigninSuccess , emailSigninFailed} from './user.action';

import { GOOGLE_SIGNIN_START,EMAIL_SIGNIN_START } from '../../types/types';

import { auth, googleProvider , createUserProfileDocument } from '../../firebase/firebase.utils';

export function* signInWithGoogle() {
    try {
        const { user } = yield auth.signInWithPopup(googleProvider);
        const userRef = yield call(createUserProfileDocument , user )
        const userSnapShot = yield userRef.get()
        yield put(googleSigninSuccess({id: userSnapShot.id,...userSnapShot.data()}))
    }
    catch (error) {
        yield put(googleSigninFailed(error.Message))
    }
}

export function* signInWithEmail(obj){
    console.log(obj)
}

export function* onGoogleSignInStart() {
    yield takeLatest(GOOGLE_SIGNIN_START , signInWithGoogle)
}

export function* onEmailSignInStart(){
    const a = yield takeLatest(EMAIL_SIGNIN_START , signInWithEmail)
    console.log("--",a)
}

export function* userSaga(){
    yield all([
        call(onGoogleSignInStart),
        call(onEmailSignInStart)
    ])
}
