import {TOGGLE_CART_DROPDOWN} from  '../../types/types';

const INITIAL_STATE = { 
    hidden : 'true'
}

const CartReducer = (state = INITIAL_STATE , action) => {

    switch (action.type) {
        case TOGGLE_CART_DROPDOWN :
            return {
                ...state ,
                hiddem : !state.hidden
            }

        default:
            return state
    }

}

export default CartReducer;
